@SuppressLint("UnusedMaterial3ScaffoldPaddingParameter")
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun Design(context: Context? = null) {

}

@SuppressLint("ServiceCast")
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun Shayari(scope: LazyItemScope, shayari: String, context: Context? = null) {
    Card (
        modifier = Modifier
            .fillMaxWidth()
            .padding(8.dp),
        colors = CardDefaults.elevatedCardColors(containerColor = Color(0xFFAA8DFA))
    ) {
        Column(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp)
        ) {
            Box(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(10.dp)

                    .background(Color.Transparent)
            ) {
                Text(
                    text = shayari,
                    textAlign = TextAlign.Center,
                    fontSize = 20.sp,
                    color = Color(0xFF9DF6ED)
                )
            }
        }
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .size(50.dp)
                .padding(1.dp)
                .background(Color.Transparent)
        ) {
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .size(50.dp)
                    .weight(1f)
                    .background(Color.Transparent)
            ) {

                Image(painter = painterResource(R.drawable.share),
                    contentDescription = null,
                    alignment = Alignment.TopStart,
                    modifier = Modifier
                        .size(30.dp, 30.dp)
                        .clickable {


                            /*Create an ACTION_SEND Intent*/
                            val intent = Intent(Intent.ACTION_SEND)

                            /*This will be the actual content you wish you share.*/
                            val shareBody = shayari

                            /*The type of the content is text, obviously.*/
                            intent.setType("text/plain")

                            /*Applying information Subject and Body.*/
                            intent.putExtra(Intent.EXTRA_SUBJECT, "")
                            intent.putExtra(Intent.EXTRA_TEXT, shareBody)

                            /*Fire!*/
                            context!!.startActivity(Intent.createChooser(intent, shayari))


                        })
            }
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .size(50.dp)
                    .weight(1f)
                    .background(Color.Transparent)
            ) {
                Image(
                    painter = painterResource(R.drawable.whatsapp4),
                    contentDescription = null,
                    modifier = Modifier
                        .size(90.dp, 30.dp)
                        .clickable {
                            val whatsappIntent = Intent(Intent.ACTION_SEND)
                            whatsappIntent.setType("text/plain")
                            whatsappIntent.setPackage("com.whatsapp")
                            whatsappIntent.putExtra(
                                Intent.EXTRA_TEXT, shayari
                            )
                            try {
                                context!!.startActivity(whatsappIntent)
                            } catch (ex: ActivityNotFoundException) {
                                Toast
                                    .makeText(
                                        context!!,
                                        shayari,
                                        Toast.LENGTH_SHORT
                                    )
                                    .show()
                            }

                        },
                    alignment = Alignment.TopCenter

                )
            }
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .size(50.dp)
                    .weight(1f)
                    .background(Color.Transparent)
            ) {
                Image(painter = painterResource(R.drawable.copy7),
                    contentDescription = null,
                    modifier = Modifier
                        .size(90.dp, 20.dp)
                        .clickable {

                            val clipboard =
                               context!!.getSystemService(CLIPBOARD_SERVICE) as ClipboardManager?
                            val clip = ClipData.newPlainText("Copy", shayari)
                            clipboard!!.setPrimaryClip(clip)
                            Toast
                                .makeText(context!!, "copy", Toast.LENGTH_SHORT)
                                .show()
                        })

            }
        }
    }
}




